import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { apiRequest } from "../api/apiRequest";
import "../Signup_Login.css";

const Login = () => {
  const navigate = useNavigate();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleLogin = async () => {
    if (!email || !password) {
      setErrorMessage("Please enter both email and password.");
      return;
    }

    try {
      const response = await apiRequest("login", "POST", "", { email, password });
      if (response.success) {
        localStorage.setItem("token", response.data.token); //Ensure JWT bearer token is stored in the local storage after logging in
        console.log("Login successful:", response.data);
        navigate("/home");
      } else {
        setErrorMessage(response.message || "An unknown error occurred.");
      }
    } catch (error) {
      console.error("Login error:", error);
      setErrorMessage("An error occurred during login. Please try again.");
    }
  };

  return (
    <div class="main-container">
    <div class="logo-box">
      Landing Page Content
    </div>

    <div class="form-container">
      <div class="tab-buttons">
        <div class="button-tab-signup">Sign Up</div>
        <div class="button-tab-login">Login</div>
      </div>

      <div class="form-section">
        <input type="text" class="input-field" placeholder="Enter your username" />
        <div class="error-message">Error: Please fill out this field.</div>
        <button class="confirm-button">Confirm</button>
      </div>
    </div>
  </div>

  );
};

export default Login;
