import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom"; // Import navigation hook
import { apiRequest } from "../api/apiRequest";

const Home = () => {
    const navigate = useNavigate(); // Initialize navigate for routing

    useEffect(() => {
        console.log("Testing API Endpoint");
        // Test the GET all users endpoint
        const fetchAllUsers = async () => {
            try {
                const response = await apiRequest("users", "GET", "");
                if (response.success) {
                    console.log("Fetched users:", response.data);
                } else {
                    console.error("Error fetching users:", response.message);
                }
            } catch (error) {
                console.error("Error:", error);
            }
        };

        // Test the GET user by ID endpoint
        const fetchUserById = async (userId: string) => {
            try {
                const response = await apiRequest("users", "GET", userId);
                if (response.success) {
                    console.log(
                        `Fetched user with ID ${userId}:`,
                        response.data
                    );
                } else {
                    console.error(
                        `Error fetching user with ID ${userId}:`,
                        response.message
                    );
                }
            } catch (error) {
                console.error("Error:", error);
            }
        };

        // Test the POST register endpoint
        const registerUser = async () => {
            try {
                const response = await apiRequest("users", "POST", "register", {
                    username: "newuser5",
                    email: "newuser5@example.com",
                    password: "password123",
                    isAdmin: false,
                    isSuperAdmin: false,
                });
                if (response.success) {
                    console.log("User registered successfully:", response.data);
                } else {
                    console.error("Error registering user:", response.message);
                }
            } catch (error) {
                console.error("Error:", error);
            }
        };

        // Call the test functions
        console.log("Testing API Endpoints:");
        registerUser();
        fetchAllUsers();
        fetchUserById("60d21b4667d0d8992e610c85"); // Replace with a valid user ID
    }, []);

    return (
        <div style={{ textAlign: 'center', padding: '20px' }}>
            {/* Logo section */}
            <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '20px' }}>
                <div
                    style={{
                        width: '300px',
                        height: '150px',
                        backgroundColor: '#3C73A8',
                        color: 'white',
                        borderRadius: '10px',
                        display: 'flex',
                        justifyContent: 'center',
                        alignItems: 'center',
                        fontSize: '18px',
                        marginRight: '20px',
                    }}
                >
                    (Logo) Eleos
                </div>
            </div>

            {/* Log In and Sign Up Buttons */}
            <div>
                <button
                    onClick={() => navigate("/login")}  // Navigate to login page
                    style={{
                        padding: '10px 20px',
                        marginRight: '10px',
                        backgroundColor: '#ADD8E6',
                        border: '1px solid #ADD8E6',
                        borderRadius: '20px',
                        cursor: 'pointer',
                        fontSize: '16px'
                    }}
                >
                    Log In
                </button>
                <button
                    onClick={() => navigate("/signup")}  // Navigate to sign up page
                    style={{
                        padding: '10px 20px',
                        backgroundColor: '#FFFFFF',
                        border: '1px solid #ADD8E6',
                        borderRadius: '20px',
                        cursor: 'pointer',
                        fontSize: '16px'
                    }}
                >
                    Sign Up
                </button>
            </div>
        </div>
    );
};

export default Home;
