import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { apiRequest } from "../api/apiRequest"; // Ensure this points to your API request function
import "../Signup_Login.css"; // Import the CSS file

const Login = () => {
    const navigate = useNavigate();

    // State variables to manage form inputs
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [errorMessage, setErrorMessage] = useState("");

    // Function to handle login

    const handleLogin = async () => {
        if (!email || !password) {
            setErrorMessage("Please enter both email and password.");
            return;
        }

        try {
            const response = await apiRequest("login", "POST", "", { email, password }); // Adjust API endpoint and request as needed
            if (response.success) {
                // Redirect or handle successful login
                console.log("Login successful:", response.data);
                navigate("/home"); // Redirect to a dashboard or home page after login
            } else {
                // Provide a fallback for response.message
                setErrorMessage(response.message || "An unknown error occurred."); // Show error message
            }
        } catch (error) {
            console.error("Login error:", error);
            setErrorMessage("An error occurred during login. Please try again.");
        }
    };


    return (
        <div className="home-container">
            <div className="flex-container">
                {/* Logo section */}
                <div className="logo-section">
                    <div className="logo-box">
                        "Login Page Content"
                    </div>
                </div>

                {/* Log In Section */}
                <div className="button-section">
                    <h2>Login</h2>
                    <div>
                        <input
                            type="email"
                            placeholder="Email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            className="input-field"
                        />
                    </div>
                    <div>
                        <input
                            type="password"
                            placeholder="Password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            className="input-field"
                        />
                    </div>
                    {errorMessage && <p className="error-message">{errorMessage}</p>}
                    <button
                        onClick={handleLogin}
                        className="button button-login-complete"
                    >
                        Log In
                    </button>
                    <div>
                        <button
                            onClick={() => navigate("/signup")}
                            className="button button-signup"
                        >
                            Sign Up
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );   
};

export default Login;
